package net.sourceforge.plantuml.app;

import net.sourceforge.plantuml.list.LinkedList;

import static net.sourceforge.plantuml.utilities.StringUtils.join;
import static net.sourceforge.plantuml.utilities.StringUtils.split;
import static net.sourceforge.plantuml.app.MessageUtils.getMessage;

import org.apache.commons.text.WordUtils;

public class App {
    public static void main(String[] args) {
        LinkedList tokens;
        tokens = split(getMessage());
        String result = join(tokens);
        System.out.println(WordUtils.capitalize(result));
    }
}
